body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
         {
            "type": "TextBlock",
            "size": "large",
            "weight": "bolder",
            "text": "Suspicious IP - Azure Sentinel",
            "wrap": true
        },
               {
            "type": "TextBlock",
            "text": "@{triggerBody()?['object']?['properties']?['severity']} incident @{triggerBody()?['object']?['properties']?['title']} ",
            "wrap": true,
            "weight": "Bolder"
        },
        {
            "type": "TextBlock",
            "text": "Incident description",
            "wrap": true,
            "weight": "Bolder"
        },
        {
            "type": "TextBlock",
            "text": " @{triggerBody()?['object']?['properties']?['description']}",
            "wrap": true
        },
        {
            "type": "TextBlock",
            "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
            "wrap": true
        },

        {
            "type": "TextBlock",
            "text": "IP Address : @{items('Each_malicious_IP_Address_Entity_present_in_the_Incident')?['Address']}",
                "wrap": true,
            "weight": "Bolder"

        },
        {
            "type": "TextBlock",

            "weight": "bolder",
            "text": "Virus Total Report",
            "wrap": true
        },
                        {
                            "type": "TextBlock",
                            "text": "Country :  @{body('Ip_scan_report_V3')?['data']?['attributes']?['country']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Continent :  @{body('Ip_scan_report_V3')?['data']?['attributes']?['continent']}",
                            "wrap": true
                        },
                       {
                            "type": "TextBlock",
                            "text": "ASN :  @{body('Ip_scan_report_V3')?['data']?['attributes']?['asn']}",
                            "wrap": true
                        },
                     {
                            "type": "TextBlock",
                            "text": "As_Owner : @{body('Ip_scan_report_V3')?['data']?['attributes']?['as_owner']}",
                            "wrap": true
                        },
 {
            "type": "TextBlock",

            "weight": "bolder",
            "text": "Total Votes :  ",
            "wrap": true
        },
                        {
                            "type": "TextBlock",
                            "text": "Malicious : @{body('Ip_scan_report_V3')?['data']?['attributes']?['last_analysis_stats']?['malicious']}",
                            "wrap": true
                        },
                       {
                            "type": "TextBlock",
                            "text": "Positive Votes : @{body('Ip_scan_report_V3')?['data']?['attributes']?['total_votes']?['harmless']} ",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "text": "Suspicious : @{body('Ip_scan_report_V3')?['data']?['attributes']?['last_analysis_stats']?['suspicious']}",
                            "wrap": true
                        },
                         {
                            "type": "TextBlock",
                            "text": "Number of reports saying that is Undetected : @{body('Ip_scan_report_V3')?['data']?['attributes']?['last_analysis_stats']?['undetected']}",
                            "wrap": true
                        },
    {
                            "type": "TextBlock",
                            "text": "Network :  @{body('Ip_scan_report_V3')?['data']?['attributes']?['network']}",
                            "wrap": true
                        },

        {
            "type": "ColumnSet",
            "columns": [
               {
                    "type": "Column",
                    "items": [

                        {
                            "type": "TextBlock",
                            "size": "Medium",
                            "weight": "Bolder",
                            "text": "Incident configuration:",
                            "wrap": true
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Azure Sentinal incident?"
        },
        {
            "choices": [
                {
                    "isSelected": true,
                    "title": "Close incident - False Positive",
                    "value": "FalsePositive"
                },
                {
                    "title": "Close incident - True Positive",
                    "value": "TruePositive"
                },
                {
                    "title": "Close incident - Benign Positive",
                    "value": "BenignPositive"
                },
                {
                    "title": "Undetermined",
                    "value": "Undetermined"
                }
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "BenignPositive"
        },
        {
            "type": "TextBlock",
            "text": "Change Azure Sentinel Incident Severity?"
        },
        {
            "choices": [
                {
                    "isSelected": true,
                    "title": "High",
                    "value": "High"
                },
                {
                    "title": "Medium",
                    "value": "Medium"
                },
                {
                    "title": "Low",
                    "value": "Low"
                },
                {
                    "title": "Don't change",
                    "value": "same"
                }
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "@{triggerBody()?['object']?['properties']?['severity']}"
        },
        {
            "type": "TextBlock",
            "text": "Azure Firewall Actions",

            "weight": "Bolder"
        },
         {
            "type": "Image",
            "style": "Person",
            "url": "data:image/png;base64,",
            "size": "Small"
        }
    ],
    "actions": [
        {
            "type": "Action.ShowCard",
            "title": "Add IP address to threat intel allow list",
            "card": {
                "type": "AdaptiveCard",
                "body": [
  {
            "type": "TextBlock",
            "text": "Select firewall policy name"
        },
        {     
      "type": "Input.ChoiceSet",
      "id": "FirewallPolicySelectedVal",
      "label": "Please select Firewall name and Rules name",
      "style": "compact",
      "isRequired": true,
      "errorMessage": "This is a required input",
      "placeholder": "Please choose",
      "choices": @{body('Select_Firewall_Policies_Choice_List_to_show_in_the_Adaptive_Card')}
    }

                            ],
                "actions": [
                    {
                        "type": "Action.Submit",
                        "title": "OK"
                    }    
                ],
                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
                "version":"1.2"
            }

        },
        {
            "type":"Action.Submit",
            "title":"Ignore"
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}
