body.messageBody:
{
  "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",

  "body": [
    {
      "size": "large",
      "text": "Suspicious Activity - Azure Sentinel",
      "type": "TextBlock",
      "weight": "bolder",
      "wrap": true
    },
 {
      "text": "Incident Configuration",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },   {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        },

    {
      "text": "Incident Title: @{triggerBody()?['object']?['properties']?['title']} ",
      "type": "TextBlock",
      "weight": "bolder",
      "wrap": true
    },
    {
      "text": "Incident #: @{triggerBody()?['object']?['properties']?['incidentNumber']} ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
 {
      "text": "Incident Severity: @{body('Post_an_Adaptive_Card_to_a_Teams_channel_and_wait_for_a_response')?['data']?['incidentSeverity']}",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "text": "Incident Description: ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "text": " SOC has provided the response as shown below.",
      "type": "TextBlock",
      "wrap": true
    },
{
      "text": "Incident Status: Closed",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
      "type": "TextBlock",
      "wrap": true
    },
    {
      "size": "Medium",
      "text": "Forcepoint NGFW:",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "size": "Small",
      "style": "Person",
      "type": "Image",
      "url": "https://upload.wikimedia.org/wikipedia/en/b/b6/Forcepoint.jpg"
    },
    {
      "text": "Malicious IP Address ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },


    {
      "columns": [
        {
          "bleed": true,
          "items": [
            {
              "text": "IP Address",
              "type": "TextBlock"
            }
          ],
          "type": "Column",
          "weight": "Bolder",
          "width": "stretch"
        },

        {
          "bleed": true,
          "items": [
            {
              "text": "SOC Action",
              "type": "TextBlock"
            }
          ],
          "type": "Column",
          "weight": "Bolder",
          "width": "stretch"
        }
      ],
      "style": "emphasis",
      "type": "ColumnSet"
    },
   @{outputs('Remove_first_bracket_from_SOC_response')}
  ],
  "type": "AdaptiveCard",
  "version": "1.2"
}

