{
  "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
  "actions": [
    {
      "title": "Submit",
      "type": "Action.Submit"
    },
 {
      "title": "Skip this Incident",
      "type": "Action.Submit"
    }
  ],
  "body": [
    {
      "size": "large",
      "text": "Suspicious Activity - Azure Sentinel",
      "type": "TextBlock",
      "weight": "bolder",
      "wrap": true
    },
    {
      "text": "Possibly Comprised IP addresses are detected by Sentinel",
      "type": "TextBlock",
      "wrap": true
    },
    {
      "text": "Incident Title: @{triggerBody()?['object']?['properties']?['title']} ",
      "type": "TextBlock",
      "weight": "bolder",
      "wrap": true
    },
    {
      "text": "Incident #: @{triggerBody()?['object']?['properties']?['incidentNumber']} ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
  {
      "text": "Incident Severity: @{triggerBody()?['object']?['properties']?['severity']} ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "text": "Incident Description: ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "text": "Sentinel found the following IP address(es) to be potentially malicious and blocked by the firewall.",
      "type": "TextBlock",
      "wrap": true
    },
    {
      "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
      "type": "TextBlock",
      "wrap": true
    },
    {
      "size": "Medium",
      "text": "Forcepoint NGFW:",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "size": "Small",
      "style": "Person",
      "type": "Image",
      "url": "https://upload.wikimedia.org/wikipedia/en/b/b6/Forcepoint.jpg"
    },
    {
      "text": "Malicious IP Address ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },
    {
      "text": "The below list of IP address exists in the policy @{variables('Firewall Policy Name')}.",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },

    {
      "columns": [
        {
          "bleed": true,
          "items": [
            {
              "text": "IP Address",
              "type": "TextBlock"
            }
          ],
          "type": "Column",
          "weight": "Bolder",
          "width": "stretch"
        },
        {
          "bleed": true,
          "items": [
            {
              "text": "Status",
              "type": "TextBlock"
            }
          ],
          "type": "Column",
          "weight": "Bolder",
          "width": "stretch"
        },
        {
          "bleed": true,
          "items": [
            {
              "text": "Action",
              "type": "TextBlock"
            }
          ],
          "type": "Column",
          "weight": "Bolder",
          "width": "stretch"
        }
      ],
      "style": "emphasis",
      "type": "ColumnSet"
    },
  @{outputs('Remove_first_bracket_from_column_set')}  , {
      "text": "Incident Configuration",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    },   {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        },
    {
      "text": "Close Azure Sentinel incident?",
      "type": "TextBlock"
    },
    {
      "choices": [
        {
          "isSelected": true,
          "title": "Close incident - False Positive",
          "value": "FalsePositive - IncorrectAlertLogic"
        },
        {
          "title": "Close incident - True Positive",
          "value": "TruePositive - SuspiciousActivity"
        },
        {
          "title": "Close incident - Benign Positive",
          "value": "BenignPositive - SuspiciousButExpected"
        },
        {
          "title": "Undetermined",
          "value": "Undetermined"
        }
      ],
      "id": "incidentStatus",
      "style": "compact",
      "type": "Input.ChoiceSet",
      "value": "BenignPositive - SuspiciousButExpected"
    },
    {
      "text": "Change Azure Sentinel incident severity?",
      "type": "TextBlock"
    },
    {
      "choices": [
        {
          "title": "Medium",
          "value": "Medium"
        },
        {
          "title": "High",
          "value": "High"
        },
        {
          "title": "Low",
          "value": "Low"
        },
        {
          "title": "Don't change",
          "value": "same"
        }
      ],
      "id": "incidentSeverity",
      "style": "compact",
      "type": "Input.ChoiceSet",
      "value": "@{triggerBody()?['object']?['properties']?['severity']}"
    },    {
      "text": "Note* : If you want to ignore the incident then click on Skip this Incident button below. ",
      "type": "TextBlock",
      "weight": "Bolder",
      "wrap": true
    }
  ],
  "type": "AdaptiveCard",
  "version": "1.2"
}
