content.query:
let attacks=DynatraceAttacks_CL
    | project id_s=attackId_s, timestamp_d, type_s="attacks";

let problems=DynatraceProblems_CL
    | project id_s=problemId_s, timestamp_d=startTime_d, type_s="observabilityproblems";

let securityProblems=DynatraceSecurityProblems_CL
    | project id_s=securityProblemId_s, timestamp_d=lastUpdatedTimestamp_d, type_s="securityproblems";

attacks
| union problems, securityProblems
| summarize  arg_max(timestamp_d, *) by id_s
| summarize  count() by type_s
| render piechart;
