let minTime = toscalar(InformationProtectionEvents | where isnotempty(MachineName) | summarize min(Time));
let dates = range ["date"] from bin(minTime, {TimeRange:grain}) to now() step {TimeRange:grain};
InformationProtectionEvents
| where isnotempty(MachineName)
| summarize labels=countif(isnotempty(ItemPath) and Activity in ("NewLabel", "UpgradeLabel", "DowngradeLabel", "RemoveProtection", "NewProtection", "ChangeProtection") and isnotempty(LabelId)),             protected=countif(isnotempty(ItemPath) and Activity in ("NewLabel", "UpgradeLabel", "DowngradeLabel", "RemoveProtection", "NewProtection", "ChangeProtection") and IsProtected) by bin(Time, {TimeRange:grain})
| join kind= rightouter (
    dates
)
on $left.Time == $right.["date"]
| project ["date"], Labels = coalesce(labels, 0), ["Protected Labels"] = coalesce(protected, 0)
